import React, {useEffect} from 'react'
import {Link} from 'react-router-dom'
import {Row, Col, ListGroup, Image, Form, Card, Button} from  'react-bootstrap'
import {useDispatch, useSelector} from 'react-redux'
import Message from '../../components/Message'
import AddToCart, { addToCart } from '../../actions/cartActions'

const CartScreen = ({match, location, history}) => {

  const productId = match.params.id
  const qty = location.search ? Number(location.search.split('=')[1]) : 1

  const dispatch = useDispatch()

  const cart = useSelector(state => state.cart)

  const { cartItems } = cart

  // console.log(cartItems);


  useEffect(() => {
    if(productId) {
      dispatch(addToCart(productId, qty))
    }
  }, [dispatch, productId, qty])

  const removeFromCartHandler = (id) => {

  }
  return (
    <Row>
      <Col md={8}>
        <h1>Shopping Cart</h1>
        {cartItems.length === 0 ?( <Message>Your cart is empty <Link to='/'>Go Back</Link></Message>) : (
          <ListGroup variant='flush'>
            {cartItems.map(item => (
              <ListGroup.Item key={item.product}>
                <Row>
                  <Col md={2}>
                    <Image src={item.image} alt={item.name} fluid rounded />
                  </Col>
                  <Col md={3}>
                    <Link to={`/product/${item.product}`}>{item.name}</Link>
                  </Col>
                  <Col md={2}>${item.price}
                  </Col>
                  <Col md={2}>
                    <Form.Control as='select' value={item.qty} onChange={(e) => dispatch(addToCart(item.product, Number(e.target.value)))}>
                    {[...Array(item.countInStock).keys()].map((p) => (
                      <option key={p + 1} value={p + 1}>{p + 1}</option>
                    ))}
                    </Form.Control>
                  </Col>
                  <Col md={2}>
                      <Button type='button' variant='light' onClick={() => removeFromCartHandler(item.product)}>
                        <span className='fas fa-trash'></span>
                      </Button>
                  </Col>
                </Row>
              </ListGroup.Item>
            ))}
          </ListGroup>
        )}
      </Col>
      <Col md={4}>
        <Card>
        <ListGroup variant='flush'>
          <ListGroup.Item>
                      <h2>
                      Subtotal ({cartItems.reduce((acc, item) => acc + item.qty, 0)}) items</h2>
                      ${cartItems.reduce((acc, item) => acc + item.qty * item.price, 0).toFixed(2)}
          </ListGroup.Item>
        </ListGroup>
           
        </Card>
      </Col>
    </Row>
  )
}

export default CartScreen
